from time import sleep

class Solution:
    def longestValidParentheses(self, s):
        """
        :type s: str
        :rtype: int
        """
        self.result = 0

        def slave(item):

            flag = 0
            start = 0

            for i in range(len(item)):
                if item[i] == '(':
                    flag += 1
                else:
                    flag -= 1

                if flag == 0:
                    finish = i
                    self.result = max(self.result, finish - start + 1)
                elif flag < 0:
                    start = i + 1
                    flag = 0

            if flag > 0:
                flag = 0
                start_r = len(item) - 1
                for j in range(len(item) - 1, start - 1, -1):
                    if item[j] == ')':
                        flag += 1
                    else:
                        flag -= 1

                    if flag == 0:
                        finish = j
                        self.result = max(self.result, start_r - finish + 1)
                    elif flag < 0:
                        start_r = j - 1
                        flag = 0

        slave(s)

        return self.result

